	/**
	 * 将数组排序 2.定义三个指针，i，j，k。遍历i，
	 * 那么这个问题就可以转化为在i之后的数组中寻找nums[j]+nums[k]=-nums[i]这个问题，也就将三数之和问题转变为二数之和---
	 * @param nums
	 * @return
	 */
	public static List<List<Integer>> threeSum1(int[] nums) {
		Arrays.sort(nums);
		List<List<Integer>> ret = new ArrayList<>();
		int length = nums.length;
		for (int i = 0; i < length; i++) {
			//要求结果列唯一，所以相同的num[i]可以说省略
			if (i > 0 && nums[i] == nums[i - 1]) continue;
			//在i之后的数组里找到  两个相加等于 -nums[i]
			int left = i+1;
			int right = length-1;
			while (left < right) {
				int sum = nums[left] + nums[right];
				if (nums[i] == sum * -1) {
					ret.add(Arrays.asList(nums[i],nums[left],nums[right]));
					while(left<right&& nums[left]== nums[left]) left++;
					while(left<right&& nums[right]== nums[right]) right++;
					left++;
					right--;
				}else if (nums[i] < sum * -1){
					right-=1;
				}else {
					left++;
				}
			}

		}
		return ret;
	}


	/**
	 * 暴力破解
	 * 超时。。。
	 * @param nums
	 * @return
	 */
	public static List<List<Integer>> threeSum(int[] nums) {
		Arrays.sort(nums);
		List<List<Integer>> ret = new ArrayList<>();
		Map<Integer, Integer> map = new HashMap();
		int length = nums.length;
		Map<String,int[]> map1 = new HashMap<>();
		for (int i = 0; i < length; i++) {
			//if (i > 0 && nums[i] == nums[i - 1]) continue;
			for (int j = i + 1; j < length; j++) {
				int sum = nums[i] + nums[j];
				int key = sum * -1;
				if (map.containsKey(key)&&
					map.get(key)!=i&&map.get(key)!=j) {
					//去除重复
					int[] retnums= {nums[i],nums[j],key};
					Arrays.sort(retnums);
					String str = ""+retnums[0] + retnums[1] + retnums[2];
					map1.put(str, retnums);
					//List<Integer> list = Arrays.asList(nums[i], nums[j], key);
					//ret.add(list);
				}
			}
			map.put(nums[i], i);
		}
		for (int[] ints : map1.values()) {
			ret.add(Arrays.asList(ints[0],ints[1],ints[2]));
		}
		return ret;
	}

